UPDATE `locales_quest` SET `OfferRewardText_loc8`='Тревожные новости.\n\nМой народ скоро покинет эти места. Мы собираемся прорваться через боевые порядки Плети, чтобы добраться до нашей столицы, Ледяной Пыли, что на Драконьем Погосте.\n\nНо прежде чем мы сделаем это, нужно разобраться с тем, что натворили гномы в своем безумии.', `RequestItemsText_loc8`='Ну что, все воронки отмечены на карте?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=11890;
