UPDATE `locales_quest` SET `OfferRewardText_loc8`='Отлично, |3-6($C), положи их пока на землю перед троном Агмара.\n\n<На лице изображения проступает тень улыбки.>\n\nУвы, судя по Горамошу, соглашение уже достигнуто...\n\nТем не менее у нас теперь меньше на двух змеев, из-за которых следует тревожиться. Займемся более важными делами!', `RequestItemsText_loc8`='Удалось ли тебе предотвратить заключение договора между синими драконами и Эфириумом?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12005;
