UPDATE `locales_quest` SET `OfferRewardText_loc8`='<Вы называете себя.>\n\nТак значит, у нас с вами есть общий враг – сыновья железа?\n\nМои войска осадили город железных дворфов, но своими силами нам не взломать их оборону. Не желаешь ли ты нам помочь?', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12074;
