UPDATE `locales_quest` SET `OfferRewardText_loc8`='Говорят, ты выбрался из переделки вместе с нашим старым-добрым дружком?\n\nПожалуй, это само по себе может считаться наградой, но тебе повезло – я сегодня щедрый.', `RequestItemsText_loc8`='У меня уже в животе бурчит, |3-6($R).Надеюсь, ты несешь мне еду?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12082;
