UPDATE `locales_quest` SET `OfferRewardText_loc8`='Это был не первый из колдунишек, которые нас предали. И, боюсь, не последний. Жаль, жаль… Это был один из лучших наших магов. Умел творить такие заклинания, что аж слюнки текли и сердце прыгало от радости…\n\nТьфу ты!', `RequestItemsText_loc8`='<Борус отшвыривает прочь свои инструменты.>\n\nВсе это бесполезно!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12089;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Тебе удалось достать этого проклятого кобыльего сына, а? Ну, что ж, одной неприятностью для наших солдат в этой мерзлой пустыне меньше.', `RequestItemsText_loc8`='<Борус отшвыривает прочь свои инструменты.>\n\nВсе это бесполезно!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12090;
