UPDATE `locales_quest` SET `OfferRewardText_loc8`='Благодарю тебя, |3-6($C).\n\nКак только я закончу слежку за этими отвратительными аптекарями и пойму, что они замышляют, я направлюсь в леса, чтобы попробовать убедить древняков присоединиться к нам.\n\nИли, если ничего не выйдет, попробую уговорить их хотя бы не нападать на тех, кто их спас!', `RequestItemsText_loc8`='Если мы сможем спасти древняков раньше, чем это сделают дриады и ночные эльфы из Покоя Звезд, быть может, они подумают о том, чтобы стать союзниками Орды.\n\nИли, по крайней мере, дважды подумают, прежде чем нападать на нас.\n\nНу так, скольких тебе удалось уже спасти?', `ObjectiveText1_loc8`='Усилено древ Лоталора', `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12096;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='ХА! Тебе удалось! Воины у Врат Гнева в долгу теперь перед тобой, $N! Теперь некоторые из них хоть на денек подольше проживут.', `RequestItemsText_loc8`='<Горт изумленно поднимает брови.>\n\nУже вернулся, так быстро?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12097;
