UPDATE `locales_quest` SET `OfferRewardText_loc8`='Я могу найти тебе кое-какое применение. Проходи-ка вперед и постой минутку.', `RequestItemsText_loc8`='И где же этот носильщик? Надеюсь, он не попадется йормунгарам… На этот раз у него с собой внушительный груз.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12112;
