UPDATE `locales_quest` SET `OfferRewardText_loc8`='Рекомендательное письмо? Тебе нужно видеть королеву? Дай-ка взглянуть…\n\nКаково бы ни было твое дело, надеюсь, оно достаточно важное. Сегодня неба не видно за крыльями нападающих синих драконов!', `RequestItemsText_loc8`='Да, |3-6($R)? Чем могу служить?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12122;
