UPDATE `locales_quest` SET `OfferRewardText_loc8`='<Горт берет у вас из рук книгу и начинает читать.>\n\nЧТО? Это не может быть правдой! Некоторых из этих орков я знаю уже много лет!\n\nОставь меня, $N. У меня много дел – и много предателей, которым нужно воздать по заслугам. Сегодня ты оказал Орде неоценимую услугу.', `RequestItemsText_loc8`='Как раз вовремя! Ты где был?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12136;
