UPDATE `locales_quest` SET `OfferRewardText_loc8`='Действительно, это очень странно, что такой предмет оказался у магнатавра... странно и очень тревожно. Я очень рада, что ты показал мне это.', `RequestItemsText_loc8`='Да, |3-6($R)?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12147;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Так, значит, это правда. Они не могли выбрать более удачного времени для такого нападения… Разрываясь между армией Плети и синими драконами, вряд ли кто-то что-то мог уже заметить…', `RequestItemsText_loc8`='Тебе удалось что-нибудь отыскать?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12148;
