UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ты и впрямь великолепен.\n\nТеперь, когда Гром\'тара Громовержца больше нет, мы можем предоставить остаткам магнатавров перебивать друг друга и вновь обратить наше внимание на куда более серьезные угрозы.\n\nВпечатляющая работа, |3-6($C).', `RequestItemsText_loc8`='Тебе удалось покончить с этой угрозой?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12151;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Дело сделано.\n\nТвоя самоотверженность даровала покой многим замученным душам.\n\nПусть это деяние освятит твою душу и снимет тяжесть с твоих плеч, ибо я предвижу, что впереди тебя ждут великие испытания.\n\nПрощай, $N.', `RequestItemsText_loc8`='Джин\'аррак пал, $N?', `ObjectiveText1_loc8`='Полководец Джин\'аррак повержен', `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12152;
