UPDATE `locales_quest` SET `OfferRewardText_loc8`='Уахахахаха!\n\nБез их лоций им больше некуда бежать теперь.\n\nПо мне так Алому Натиску конец – и надо же, как раз тогда, когда они начинали! Какая досада.\n\nХаха.', `RequestItemsText_loc8`='Только, пожалуйста, НЕ ВЗДУМАЙ говорить мне, что на их паруса не действует моя огненная вода!\n\nЯ очень не хотела бы снова проводить с ней эксперименты.', `ObjectiveText1_loc8`='Паруса \"Глупости грешника\" подожжены', `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12243;
