UPDATE `locales_quest` SET `OfferRewardText_loc8`='Этого недостаточно!\n\n<Голос сержанта дрожит от скорби по павшим товарищам, оскверненным вражеской магией.>\n\nМы должны сделать больше, чтобы почтить память наших товарищей!', `RequestItemsText_loc8`='Прядильные органы должны быть целыми, без изъянов, и тогда я смогу сделать из них устройство, которое может тебе пригодиться.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12552;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Это именно то, что нужно!\n\nНо для того, чтобы собрать катапульту, мне нужны еще и некоторые детали.', `RequestItemsText_loc8`='Прядильные органы должны быть целыми, без изъянов, и тогда я смогу сделать из них устройство, которое может тебе пригодиться.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12553;
