UPDATE `locales_quest` SET `OfferRewardText_loc8`='Отличные экземпляры!\n\nТут мы имеем явные отклонения от корневой символики. Да, интересно...\n\nЖалко, что ледяные тролли так решительно уничтожают свое собственное наследие.\n\nТы знаешь, к примеру, что сами же тролли изгнали племя Драккари – за их тягу к насилию и свирепый нрав?\n\nСобственно, поэтому им и пришлось поселиться в столь суровом и непригодном для жизни климате.', `RequestItemsText_loc8`='Драккари сами себя истребляют, но будет жаль, если будет утеряна и история их империи.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12635;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Скоро настанет время проливать кровь наших врагов. Но сначала ты $gдолжен:должна; увидеть то, что собираешься уничтожить. Нельзя безоглядно бросаться в битву, не имея представления о том, с чем тебе предстоит столкнуться. Именно это отличает рыцаря смерти от безмозглого вурдалака.$B$BМой защитник, я дарую тебе возможность заглянуть за пределы досягаемого. Ты воспользуешься оком Акеруса и познаешь тайны наших врагов.', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12636;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Теперь ты знаешь, какая судьба тебя ожидала.$b$bКак я уже говорил, это все - не случайность. Изготовление подобного устройства - дело достаточно сложное, а значит, его создавали для какой-то конкретной цели. То есть, именно для тебя.$b$bТак что все очень просто: силы Плети хотят тебя заполучить...', `RequestItemsText_loc8`='Да, |3-6($r)?', `ObjectiveText1_loc8`='Назначение колье выяснено', `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12637;
