UPDATE `locales_quest` SET `OfferRewardText_loc8`='<На глаза Ньормельда наворачиваются слезы, когда он высыпает на наковальню содержимое мешка.>\n\nРог Ходира...\n\nСвященный дар моему народу, который мы получили из рук самого Ходира, $R!\n\nКогда в следующий раз ты придешь в нашу крепость, ты увидишь плоды своих трудов.\n\nПрощай.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=12976;
