UPDATE `locales_quest` SET `OfferRewardText_loc8`='<Корм отдает честь.>\n\nбудь проклят Альянс и будь проклята Плеть! Хорк был одним из наших лучших бойцов! Мы отомстим за него. Во имя Адского Крика и всей Орды!', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=13224;
