UPDATE `locales_quest` SET `OfferRewardText_loc8`='Мои верные рыцари послужили мне на славу. Похоже, они нашли мне настоящего героя!', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=13227;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Увидев, что солдаты Альянса атакуют ворота, они напали на них с тыла...\n\n<Корм ненадолго замолкает и неожиданно разражается смехом.>\n\nВот! Вот что такое Орда!\n\nЕсли бы не они, Альянс бы уже давно захватил ворота!\n\nОтвага бойцов Орды помогла нам выиграть драгоценное время.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`='Вы побеседовали с умирающим берсерком', `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=13228;
