UPDATE `locales_quest` SET `OfferRewardText_loc8`='Молодец, |3-6($C).\n\nОчень скоро Альянс будет дрожать от страха при одном только упоминании твоего имени, а в Орде будут славить тебя, как бесстрашную воительницу!', `RequestItemsText_loc8`='Итак, |3-6($R)?\n\nКаковы твои выводы?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=13234;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Теперь, когда мы уничтожили этого мясистого великана, Король-лич поймет, что остальные его войска тоже не неуязвимы. Говорят, что Король-лич не знает, что значит страх, но с твоей помощью мы напомним ему, что это такое.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=13235;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Но способность создавать армии из трупов не была главным умением принца. Это давалось ему легко.\n\nНо вот убивать своих же солдат, с которыми не раз приходилось воевать плечом к плечу... Да. Поистине главным умением принца была способность делать то, чего требовала от него ситуация.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`='Оживлено вурдалаков-прислужников', `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=13236;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Правда-правда, $C?\n\nТебе нечего мне рассказать? Совсем?\n\n<Кольтира замечает ошметки поганищ, прилипшие к вашей одежде, и начинает улыбаться.>\n\nПозволю себе тебе не поверить...', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`='Уничтожено неповоротливых поганищ', `ObjectiveText2_loc8`='Уничтожено злокозненных некромантов', `ObjectiveText3_loc8`='Уничтожено адептов Тьмы', `ObjectiveText4_loc8`=NULL WHERE `Id`=13237;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Занят? Пфф… Тоже мне, деловой нашелся! \n\nЛадно, неважно. Сойдешь и ты...', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=13238;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ничего себе! Даже отсюда мне было видно, как они загорелись! В общем, эксперимент удался. Я отмечаю очередной успех и начинаю разливать раствор по бутылкам – в плане подготовки к вторжению. Скорее всего, нам понадобится провести еще несколько испытаний. И мы их проведем! Ну, то есть, если тебе интересно...', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`='Проведены полевые испытания', `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=13239;
