UPDATE `locales_quest` SET `OfferRewardText_loc8`='Хорошо. Прислони секиру к стене.\n\nЯ уверен, что Валгард и Лагерь Возмездия сегодня будут спать более спокойно, зная, что с этим злодеем благодаря тебе наконец-то покончено.', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=13245;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Увы, это было необходимо.\n\nОрден Кирин-Тор не мог позволить столь видной представительнице красных драконов пасть жертвой нашего величайшего врага. Если бы Малигос начал использовать ее в Войне Нексуса, настал бы конец всему.\n\nНо я горюю о том, что не могли мы спасти ее.', `RequestItemsText_loc8`='Если бы нам был известен способ, как исцелить ее, мы бы не просили тебя ее убить.\n\nНо мы знаем, насколько сильна стала власть Малигоса над магией. После его манипуляций с силовыми линиями Азерота он стал непревзойденным мастером тайной магии.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=13246;
