INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `OfferRewardText`, `RequestItemsText`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (456, 'ruRU', 'Природное равновесие', 'Мои приветствия, $n. Я – Опекун Илталайн. Здесь, в Тенистой долине, я слежу за поддержанием природного равновесия. $b$bВесенние дожди в этом году были очень сильными, и от этого одни лесные существа благоденствуют, а другие страдают.$b$bК несчастью, ночные саблезубы и щетинистые вепри слишком расплодились. Тенистая долина не может их прокормить. $gЮный:Юная; $r, помоги нам – сократи численность вепрей и саблезубов ради сохранения природного равновесия.', 'Убейте 4 молодых ночных саблезубов и 4 молодых щетинистых вепрей и вернитесь к Опекуну Илталайну.', 'Ты хорошо $Gисполнил:исполнила; свой долг, $n.', 'Дел еще немало, $n. Возвращайся ко мне, как только сократишь поголовье ночных саблезубов и щетинистых вепрей.', '', 'Вернитесь к Опекуну Илталайну в Тенистую Долину', '', '', '', '', 18019);
