INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `OfferRewardText`, `RequestItemsText`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (457, 'ruRU', 'Естественное равновесие', 'Сокращение численности молодых особей, обитающих в Тенистой долине – это неплохо для начала, $n, однако нужно выполнить еще некоторую работу.$b$bЛесные ресурсы истощатся слишком быстро, если не решить эту проблему. Охота на диких зверей – это зло, необходимое для блага всех тех, кто обитает в этих землях. Отправляйся в лес и убивай облезлых ночных саблезубов и щетинистых вепрей ради равновесия.', 'Убейте 5 облезлых ночных саблезубов и 5 щетинистых вепрей и вернитесь к Опекуну Илталайну.', 'Твои дела доказывают твою преданность природе, $n. Поверь, $c, несмотря на твою юность уже заметно, что тебя ждет блестящее будущее.', 'Твоя работа еще не завершена, $n. Возвращайся ко мне после того, как убьешь 5 облезлых ночных саблезубов и 5 щетинистых вепрей.', '', 'Вернитесь к Опекуну Илталайну в Тенистую Долину.', '', '', '', '', 18019);
