INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `OfferRewardText`, `RequestItemsText`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (916, 'ruRU', 'Яд чащобного паука', 'Я пришел в Тенистую долину, чтобы наблюдать за чащобными тенетниками, обитающими в Паучьем логове. Они – родичи более мелких разновидностей пауков. Полагаю, что Древо Жизни оказало на них сильное воздействие... Я хотел бы получить несколько вещей, чтобы изучить их и подтвердить свои догадки.$b$bДля начала мне понадобится немного их яда. Добудь ядовитые железы у пауков-тенетников в Паучьем логове и вокруг него, к северу отсюда. После этого я смогу изучить эти образцы и понять, есть ли у этого яда сходство с ядом их более мелких сородичей.', 'Принесите 10 ядовитых желез чащобных тенетников Гилшалану Буйному Ветру, стоящему у Альдрассила.', 'Благодарю тебя, $n. Когда я вернусь в Дарнас, я первым же делом сравню образцы яда из этих желез с ядами других пауков. Полагаю, что некоторые свойства яда связаны с ростом нового Древа Жизни.', 'Тебе удалось собрать ядовитые железы, $n?', '', 'Вернитесь к Гилшалану Буйному Ветру, стоящему у Альдрассила.', '', '', '', '', 18019);
