INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `OfferRewardText`, `RequestItemsText`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (917, 'ruRU', 'Яйцо чащобного тенетника', 'Теперь, когда у меня есть паучий яд, мне хотелось бы получить для изучения живой образец. Не волнуйся, я не могу просить тебя поймать гигантского паука живьем. Да и мне самому вряд ли удалось бы справиться с таким "образцом"!$b$bНо если ты принесешь яйцо, из которого еще не вылупился паучок, то получить образец будет куда легче, не говоря уж о том, чтобы содержать.$b$bВ глубинах Паучьего логова должно быть гнездо. Прошу тебя, найди его, возьми оттуда яйцо паука и принеси мне.', 'Принесите яйцо чащобного тенетника Гилшалану  Буйному Ветру, стоящему у Альдрассила.', 'О, отлично. Я отправлю яйцо и яд в Дарнас, а потом и сам вернусь туда, когда мои изыскания здесь будут завершены. Я многого ожидаю от этих образцов, $n. Твоя помощь оказалась поистине бесценной.', 'Тебе удалось проникнуть в Паучье логово, $n? А найти паучье яйцо?\r\n', '', 'Вернитесь к Гилшалану Буйному Ветру, стоящему у Альдрассила.', '', '', '', '', 18019);
