INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `OfferRewardText`, `RequestItemsText`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (920, 'ruRU', 'Зов Тенарона', 'Ты $n, не так ли? До меня доходили слухи, что тебя искал Тенарон. Ты отыщешь его в самой верхней комнате на вершине Альдрассила.$b$bХоть мы и живем долго, не стоит заставлять Тенарона ждать.', 'Поговорите с Тенароном Хваткой Бури. Он ждет вас на вершине Альдрассила в Тенистой долине.', 'А, $n. Я думал, что ты быстрее откликнешься на мой призыв. У меня есть важное задание, которое я хочу поручить тебе.', '', '', 'Поговорите с Тенароном Хваткой Бури. Он ждет вас на вершине Альдрассила в Тенистой долине.', '', '', '', '', 18019);
