INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `OfferRewardText`, `RequestItemsText`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (921, 'ruRU', 'Телдрассил: венец Азерота', 'Пришла пора тебе отправиться на поиски приключений, $n. Однако прежде чем ты покинешь эти волшебные леса, тебе следует узнать побольше о нашей современной истории.$b$bУ меня есть для тебя одно задание. Отправляйся к лунному колодцу, расположенному к северу от Альдрассила, и наполни вот этот фиал колодезной водой. Находясь там, ты сможешь приобщиться к нашей магии, а также заглянуть в прошлое нашего народа.$B$BНабрав воды, поднимись по западному помосту на вершину Альдрассила и отдай фиал Тенарону Хватке Бури.', 'Наполните хрустальный фиал водой и отнесите его Тенарону Хватке Бури, обитающему на вершине Альдрассила.', 'Тебе уже довелось услышать первую часть истории о последствиях битвы за гору Хиджал, однако еще многое нужно рассказать, и выполнение задания, данного мной тебе, будет продолжаться на протяжении всего твоего путешествия через Тельдрассил в Дарнас.', 'Лунные колодцы содержат воду Источника Вечности, древнего источника магии, который навлек на наш мир так много ужасов.$b$bДруиды пользуются свойствами колодцев, а Стражи чтят их как святилища Элуны, но использовать магию колодцев запрещено всем.', '', 'Поднимитесь по западному помосту на вершину Альдрассила и поговорите с Тенароном Хваткой Бури.', '', '', '', '', 18019);
