INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `OfferRewardText`, `RequestItemsText`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (458, 'ruRU', 'Защитница леса', 'Какая отрада, что ты здесь, |3-6($r). Шепот лесных духов донес до моего слуха странные вести.$b$bТаинственная защитница леса, Тариндрелла, вернулась в Тенистую долину. Много лет эту дриаду не видели в лесах Калимдора. Что-то определенно не так, если она вновь появилась в этих землях. $b$bНайди Тариндреллу и узнай, что привело ее в наши рощи. Кто-то из Стражей сообщил о том, что ее видели к юго-западу от Альдрассила.', 'Найдите дриаду по имени Тариндрелла.', 'Я вижу, тебе удалось найти меня, $gюный:юная; |3-6($r)! Друид Мелитар мудро поступил, отправив тебя сюда.', '', '', '', '', '', '', '', 18019);
