INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `OfferRewardText`, `RequestItemsText`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (459, 'ruRU', 'Демонические воры', 'Непонятное зло затаилось в лесу Тельдрассила. Взгляни на холмы, где некогда обитали мирные фурболги. Они покинули свои дома и объединились в племя Кривой Сосны.$b$bЛишь порча, вызванная зловещим мхом Скверны, могла быть причиной подобного изменения. Грелли и греллиды кишат в округе, представляя собой угрозу для жителей Тенистой долины. $b$bНапади на этих бесов, $n, и узнай, действительно ли они подпали под чары зловещего мха Скверны.', 'Соберите 8 порций мха Скверны и принесите их Тариндрелле.', 'Твое служение обитателям Тенистой долины достойно награды, $n.$b$bОднако мои опасения подтвердились. Если даже грелли попали под влияние мха Скверны, то можно вообразить, что случилось с фурболгами племени Кривой Сосны, некогда жившими здесь.$b$b$r, если будешь в Доланааре, отыщи мудрого друида Атридаса Медвежью Шкуру. Он, как и мы, печется о благополучии леса.', 'Помоги мне подтвердить или опровергнуть мои подозрения, $n. Принеси мне 8 кусочков мха Скверны.', '', 'Вернитесь к Тариндрелле', '', '', '', '', 18019);
