INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `OfferRewardText`, `RequestItemsText`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (4495, 'ruRU', 'Добрый друг', 'Мой друг по имени Иверрон обычно навещает меня в одно и то же время каждый день. Странно, что сегодня он вообще не пришел – он задерживается уже на несколько часов.$B$BПризнаться, $n, я несколько волнуюсь. Иверрон проводит много времени возле пещеры к северу отсюда, и я знаю, насколько там опасно – это ведь логово пауков!$B$BПослушай, если ты вдруг направляешься в ту сторону, то не поищешь ли Иверрона?', 'Найдите Иверрона возле пещеры на севере.', 'Я рад, что тебе удалось найти меня, $n. Как ты $gузнал:узнала;, что я тут?', '', '', '', '', '', '', '', 18019);
