INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `OfferRewardText`, `RequestItemsText`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (3519, 'ruRU', 'Друг в беде', 'Ох... меня сильно покусала паучиха по имени Гитьис Зловещая, когда я обследовал паучиную пещеру. Похоже, я серьезно отравлен. Помоги мне!$B$BПожалуйста, дай знать Дирании Серебряному Сиянию. Она сможет помочь мне.$B$BСпеши... Как голова кружится...', 'Поговорите с Диранией Серебряным Сиянием в Тенистой долине.', 'Ох, какая беда, а я-то думала, что это сегодня Иверрона не видно... Предупреждала же я его насчет пауков!', '', '', '', '', '', '', '', 18019);
