INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `OfferRewardText`, `RequestItemsText`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (3521, 'ruRU', 'Противоядие для Иверрона', 'Мы могли бы помочь Иверрону: я знаю противоядие, которое нейтрализует действие яда. Но для него мне нужны некоторые ингредиенты.$b$bМне понадобятся гиацинтовые грибы. Их можно найти под деревьями или добыть у греллей к югу отсюда – похоже, они им по вкусу. Еще мне нужны лунные лилии, что растут только вокруг прудов.$b$bПоследний ингредиент добыть сложнее всего. Это лимфа тех самых чащобных тенетников, что отравили Иверрона.', 'Соберите 7 гиацинтовых грибов, 4 лунные лилии и 1 порцию лимфы чащобного тенетника и принесите Дирании Серебряное Сияние в Тенистую долину.', 'Хвала Элуне – ты так быстро $gсобрал:собрала; все ингредиенты!', 'Пожалуйста, принеси мне все эти ингредиенты, $n. Иверрону нужна наша помощь!', '', 'Вернитесь к Дирании Серебряное Сияние что в Тенистой долине', '', '', '', '', 18019);
