INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `OfferRewardText`, `RequestItemsText`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (3522, 'ruRU', 'Противоядие для Иверрона', 'Противоядие готово, $n. Пожалуйста, проследи, чтобы Иверрон его выпил.$B$BИ запомни – это важно: противоядие будет обладать своими целебными свойствами в течение пяти минут. Необходимо доставить его до истечения этого времени.$B$BПоторопись, во имя Элуны!', 'Отнесите противоядие Иверрону, прежде чем оно потеряет свои целебные свойства. Иверрона можно найти около пещеры, что к северу отсюда.', 'Что это, $n?$b$bЯ знал, что Дирания поможет мне!$b$b$b*Иверрон выпивает противоядие.*$b$b$bЯ все время попадаю в переделки, а Дирания постоянно меня из них вытаскивает.$b$bМне стало намного лучше, хотя, пожалуй, я немного посижу тут, пока совсем не приду в себя. Что ж, надеюсь, ты $gрасчистил:расчистила; мне дорогу через этот лес, полный мерзостных чащобных тенетников...', 'О... $n, какое счастье, что ты $gвернулся:вернулась;...', '', '', '', '', '', '', 18019);
-- Quest ID 2159 -- Fix ruRU locale --
