INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `OfferRewardText`, `RequestItemsText`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (928, 'ruRU', 'Тельдрассил: утраченное знание', 'Я мог бы поведать тебе еще многое о лунных колодцах и о Тельдрассиле, однако тебе пора в путь. Коритрас Лунный Гнев ждет тебя. Я перелил воду из фиала, принесенного тобой, в этот сосуд, чтобы ты $gотнес:отнесла; его Коритрасу.$b$bТы найдешь Коритраса у лунного колодца в Доланааре. Иди из Тенистой долины по дороге, что на юге от Альдрассила, а когда она свернет на запад, следуй по пути, мощенному булыжниками.$b$bИ советую тебе не сходить с дороги, $n, – в последнее время в лесу появились опасные существа.', 'Отнесите частично наполненный сосуд Коритрасу Лунному Гневу в Доланаар.', 'А, понятно. Тебя послал Тенарон. Ну что ж, похоже, нам о многом нужно поговорить, многое сделать – но времени мало.$b$bДумаю, лучше не медлить и приступить к делу сейчас же.', 'Привет тебе, |3-6($r). Чем обязан удовольствию встречи с тобой?', '', '', '', '', '', '', 18019);
