INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (3701, 0, 'zhCN', '你可以说实话，尼尔鲁……', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21272, 0, 'zhCN', '真高兴可以看到暗影议会失败后燃烧之刃可以重新扛起大旗。', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21273, 0, 'zhCN', '也就是说燃烧之刃只是挡子弹的吗？', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21274, 0, 'zhCN', '如果你需要我的话……', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21276, 0, 'zhCN', '你怎么知道？我是说……是的，我正在寻找那块碎片，它在你这里吗？', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21277, 0, 'zhCN', '好的。哪里？', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21278, 0, 'zhCN', '铜须的……胡须啊！你在说什么！', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21279, 0, 'zhCN', '鱼？你给了个什么？你说拯救艾泽拉斯所有生命的关键是一条鱼？', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21280, 0, 'zhCN', '一条小鱼？大海里全都是小鱼！有一百万的好几次方条小鱼！', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21281, 0, 'zhCN', '请继续吧。', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21282, 0, 'zhCN', '你把一块鱼放在这里和东部王国之间的海域?这块鱼有这种特殊能力?', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21283, 0, 'zhCN', '你疯了。', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21284, 0, 'zhCN', '我在认真地听呢.', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21285, 0, 'zhCN', '请再说一次。', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21286, 0, 'zhCN', '好吧，我们直说吧。你把权杖放在了你在用阿纳克洛斯飞行时你自己创造的小鱼身上，现在你希望我制造一个...奥金鱼漂或者其它什么...用来抓住你那躲起来的小鱼？并且这可能招致一个元素领主的愤怒？我说错什么细节吗？也许我需要在一次日食，在闰年，并且不穿衣服的情况下去做这件事情？', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21287, 0, 'zhCN', '太棒了！还有，敢问，我如何获得一个奥金鱼漂？', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21288, 0, 'zhCN', '但是…', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (21289, 0, 'zhCN', '我相信你.', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (8336, 0, 'zhCN', '我从伊利丹大人那里带来消息.', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (8342, 0, 'zhCN', '密码片段被移除了.把它带给祖鲁希德.', NULL);
