INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (9731, 0, 'zhCN', '我准备好协助你了。', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (9731, 1, 'zhCN', '狩猎已经让我了解了达卡莱帝国。我很想帮助你克服它。', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (9731, 2, 'zhCN', '我已经把那些幽魂打变形了。', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (9731, 3, 'zhCN', '我已经杀死达玛克，我怎样才能为你做其它服务？', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (9731, 4, 'zhCN', '达卡莱头目已经被抓获，还有什么需要我去做吗？', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (9731, 5, 'zhCN', '我已经目睹了你的宏伟手段，达库鲁，我们什么时候实践你的计划？', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (9732, 0, 'zhCN', '呃，你可以送我去祖尔德拉克旅行吗？', NULL);
