INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (565, 0, 'zhCN', '因此......土灵是从石头中诞生的？', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (566, 0, 'zhCN', '关于土灵还有什么我应该知道的？', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (567, 0, 'zhCN', '我想现在我明白了造物者设计土灵的意图了。你之前说到的土地异常是什么情况？', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (568, 0, 'zhCN', '什么！？高压力环境会导致大地破碎。', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (569, 0, 'zhCN', '当大地破碎时发生了什么？', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (570, 0, 'zhCN', '穴居人？！你提到的穴居人和现在世界上的一个种族是同一个吗？', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (571, 0, 'zhCN', '当土灵不稳定时，你提到的两个结果。第二个是什麽?', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (572, 0, 'zhCN', '矮人！！你是说矮人是由土灵进化来的？', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (573, 0, 'zhCN', '这些矮人会有一天是相同的，对吗？矮人和土灵还保持着其它联系吗？', NULL);
