INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (7403, 0, 'zhCN', '我不是来杀你的，暗夜精灵。神也不这样做.....', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (7402, 0, 'zhCN', '我害怕我的人民对这些破坏进行负责，我们是难民，被燃烧军团逼着离开我们的家园。这些悲剧是我们最近一次疏散的结果。我们撞船了-这是一部分的船只残骸。', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (7401, 0, 'zhCN', '我们有太多相同的地方，暗夜精灵。我不能帮助你，但是我认为是命运让我们走到一起，让我帮助你，科伦。我们会纠正错误，我发誓！', NULL);
