INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (9302, 0, 'zhCN', '别再担心了，牦牛人。部落会拯救并保护你和你的人民，但是首先你必须拿到《部落的血誓》来宣誓向酋长效忠。', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (9303, 0, 'zhCN', '为了部落!', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (9304, 0, 'zhCN', 'Then repeat after me: \"\"Lok\'tar ogar! Victory or death - it is these words that bind me to the Horde. For they are the most sacred and fundamental of truths to any warrior of the Horde.\n\nI give my flesh and blood freely to the Warchief. I am the instrument of my Warchief\'s desire. I am a weapon of my Warchief\'s command.\n\nFrom this moment until the end of days I live and die - For the Horde!\"\"', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (9305, 0, 'zhCN', '是的，坦卡。惩罚是指定给部落成员的权利。', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (9422, 0, 'zhCN', '我好像弄丢了你的地精控制器...你还有吗？', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (9462, 0, 'zhCN', '你是我的荣耀，首领。请阅读这个卷轴上的内容。这就是《部落的血誓》。', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (9461, 0, 'zhCN', 'Greetings High Chief. Would you do me the honor of accepting my invitation to join the Horde as an official member and leader of the Taunka?', NULL);
