INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (9595, 0, 'zhCN', '那么永恒龙军团是怎么计划来扰乱的？', NULL);
