INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (9906, 0, 'zhCN', '我很抱歉打扰了您的休息, 酋长, 但是你兄弟的灵魂正处于危险之中。你能告诉我关于他的事情吗?', NULL);
