INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (7768, 1, 'zhCN', 'Garrosh is beyond redemption, Greatmother. I fear that in helping the Mag\'har, I have convinced Garrosh that he is unfit to lead.', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (7974, 0, 'zhCN', 'I have done all that I could, Greatmother. I thank you for your kind words.', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (7979, 0, 'zhCN', 'Greatmother, you are the mother of Durotan?', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (7978, 0, 'zhCN', 'Greatmother, I never had the honor. Durotan died long before my time, but his heroics are known to all on my world. The orcs of Azeroth reside in a place known as Durotar, named after your son. And... <You take a moment to breathe and think through what you are about to tell the Greatmother.>', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (7977, 0, 'zhCN', 'It is my warchief, Greatmother. The leader of my people. From my world. He... He is the son of Durotan. He is your grandchild.', NULL);
