INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (6208, 1, 'zhCN', 'I not-so-quietly ignore the insult, hoping to instill a fear in the ruler that he may have gaffed.  I then inform my liege of the insult when I return.', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (6208, 2, 'zhCN', 'I quietly ignore the insult.  I will not tell my liege, as I am to secure peace at all costs.  It\'s only an insult - not a declaration of war.', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (6209, 0, 'zhCN', 'I would speak against my brother joining the order, rushing a permanent breach in our relationship.  He would be a danger to himself and those around him, and that is too great a risk hoping he would improve over time.', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (6209, 1, 'zhCN', 'I would speak for my brother joining the order, potentially risking the safety of the order.  I could help him with the order\'s regimens, and I\'d have faith in his ability to adapt and learn.', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (6209, 2, 'zhCN', 'I would create some surreptitious means to keep my brother out of the order.  I can keep him out without him being any bit wiser, thereby saving our familial bonds.', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (6210, 0, 'zhCN', 'I would show my liege the beast\'s ear and claim the beast\'s death as my own, taking the reward for my own use.  It is wrong to claim a deed as your own that someone else in fact did.', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (6210, 1, 'zhCN', 'I would show my liege the beast\'s ear and claim the beast\'s death as my own - after all, I did slay it.  I would then offer some of the reward to the destitute knight to help his family.', NULL);
INSERT INTO `gossip_menu_option_locale`(`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES (6210, 2, 'zhCN', 'I would remain silent about the kill and allow the knight to claim the reward to aid his family.', NULL);
