INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (7810, 'zhCN', '啊，我的朋友!仅有一件饰品是不能成为竞技场高手的!现在是证明你实力的时候啦!', 0);
