INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (7815, 'zhCN', '哦，伙计!我对你感激不尽!希望你喜欢这柄钓鱼竿。', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (7816, 'zhCN', '伙计!这太棒了!我现在有了最好的鱼饵，而且海水钳嘴龟的数量也下降了不少，我的钓鱼生意一定会好很多的。', 0);
