INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (7827, 'zhCN', '太好了!我们感谢你慷慨的捐赠，$N!', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (7828, 'zhCN', '干得好，$C。恶齿巨魔感谢你的贡献。', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (7829, 'zhCN', '你已经证明了自己对恶齿巨魔来说是个不可多得的盟友，$N。做得好。', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (7830, 'zhCN', '<马克霍尔看了一下羽毛。>$B$B我真想知道这头野兽在被正法之前曾吞噬了我们多少优秀的猎人。', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (7831, 'zhCN', '太好了!我们感谢你慷慨的捐赠，$N!', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (7832, 'zhCN', '$N―如果没有像你这样的英雄为暴风城亲力亲为的话，我们肯定会碰到很多麻烦。感谢你为我们付出的努力!', 0);
