INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (8635, 'zhCN', '战争的历史是传奇茁壮的种子。你正处于英雄的时代，年轻人。愿你的英勇传奇深植人心、源远流长。$B$B祝你平安， $N，这是你应有的奖赏……', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (8636, 'zhCN', '万物皆难逃一死，但精神却永垂不朽。祝你平安， $N，这是给你的奖赏……', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (8637, 'zhCN', '你做的很好，$N。你已证明你不是普通的人类。$B$B我把这些东西给你，相信你可以帮助我们击败万恶的其拉虫人。', 0);
