INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (8705, 'zhCN', '你做的很好，$C。这是把杰作之剑……它的平衡非常完美。$B$B拿着你的武器，$N。带着无比的自信使用它并消灭我们的敌人吧!', 0);
