INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (8913, 'zhCN', '啊，是的。莫克斯一定会对这个感到非常满意。$B$B那护腕以前是大型护甲装备的一部份。如果你能再帮我忙的话，我这里还有更多可以跟你护腕搭配的装备。', 0);
