INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (9007, 'zhCN', '我真的会很想你，$N。我欠你一份人情，这是我永远也无法回报的。$B$B好好使用你的新头饰和胸甲。愿它能保护你很长一段时间，为你带来更多的荣耀!', 0);
