INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (9015, 'zhCN', '你取回了部份的勋章了吗，$N?', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (9016, 'zhCN', '就因为这个勋章，这个诅咒才会降临到我们身上。瓦萨拉克领主显然知道如何掌握仇恨。$B$B我们会尽力找到剩下的碎块 - 希望是在我面临最终的死期之前。不过在那之前，让我们先来看看你的奖励。', 0);
INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (9017, 'zhCN', '就因为这个勋章，这个诅咒才会降临到我们身上。瓦萨拉克领主显然知道如何掌握仇恨。$B$B我们会尽力找到剩下的碎块 - 希望是在我面临最终的死期之前。不过在那之前，让我们先来看看你的奖励。', 0);
