INSERT INTO quest_offer_reward_locale (ID,locale,RewardText,VerifiedBuild) VALUES (9101, 'zhCN', '别忘了未受抑制的力量和未被使用的力量具有一样的毁灭性，$C。穿上霜火护甲时要谨慎使用。', 0);
